import React,{Component} from 'react';
import {
    BrowserRouter as Router,
    Link,
    Route,
    Switch,
  } from 'react-router-dom';
import '../../App.css';

class Login extends Component{
constructor(props){
    super(props);
    this.state={
        username: "",
        password: "",
        errors: []
    };
}
validation=(elm,msg)=>{
this.setState((prevState)=>({errors:[...prevState.errors,{elm,msg}]}));
}
clearValidation=(elm)=>{
    this.setState((prevState)=>{
        let newArr=[]
        for(let err of prevState.errors)
        {
            if(err.elm!==elm)
            {
                newArr.push(err)
            }
        }
        return {errors: newArr}
    });
}
onUsernameChange=(e)=>{
    this.setState({username: e.target.value});
    this.clearValidation("username");
}
onPasswordChange=(e)=>{
    this.setState({password: e.target.value});
    this.clearValidation("password");
}
submitLogin=(e)=>{
    if(this.state.username===""){
        this.validation("username","Please enter username")
    }if(this.state.password===""){
        this.validation("password","Please enter password to verify security")
    }

}

render(){
    let usernameErr=null, passwordErr=null;
    for(let err of this.state.errors){
        if(err.elm==="username"){
            usernameErr=err.msg;
        }if(err.elm==="password"){
            passwordErr=err.msg;
        }
    }
    return(
    <div className="inner-container">
        <div className="header">
            Login
        </div>
        <div className="box">
            <div className="input-group">
                <label htmlFor="username">Username</label>
                <input type="text" name="username" className="login-input" placeholder="Username"
                onChange={this.onUsernameChange}
                />
                 <small className="danger-error">{usernameErr?usernameErr:""}</small>
            </div>

            <div className="input-group">
                <label htmlFor="password">Password</label>
                <input type="password" name="password" className="login-input" placeholder="Password"
                onChange={this.onPasswordChange}
                />
                 <small className="danger-error">{passwordErr?passwordErr:""}</small>
            </div>
            <Link to="/Rules"><button
            type="button"
            className="login-btn"
            onClick={this
            .submitLogin
            .bind(this)}>Login</button></Link>
            </div>
        </div>

    );
        }
    }

export default Login;
